async () => {
  let cycleTimes = [];
  var data = GetCycleTimes.data.data.searchClosedIssues.nodes;
  for (var i = 0; i < data.length; i++) {
    var timelineItems = data[i].timelineItems.nodes;
    for (var j = 0; j < timelineItems.length; j++) {
      if (timelineItems[j].key === "issue.change_pipeline" && timelineItems[j].data.to_pipeline.name === "In Progress" && timelineItems[j].data.workspace.name === "Data Integration Pod" && new Date(data[i].closedAt) >= new Date(StartDate.selectedDate) && new Date(data[i].closedAt) <= new Date(EndDate.selectedDate)) {
        cycleTimes.push({
          "number": data[i].number,
          "startedAt": timelineItems[j].updatedAt,
          "closedAt": data[i].closedAt,
          "cycleTime": CycleTimeUtils.getDiffWorkdays(new Date(timelineItems[j].updatedAt), new Date(data[i].closedAt))
        });
      }
    }
  }
  const unduplicatedCycleTimes = Object.values(cycleTimes.reduce((acc, curr) => {
    if (!acc[curr.number] || acc[curr.number].cycleTime > curr.cycleTime) {
      acc[curr.number] = curr;
    }
    return acc;
  }, {}));
  return unduplicatedCycleTimes;
}

async () => {
  let estimates = [0.2, 0.5, 1, 2, 3, 5, 8];
  var stats = [];
  var globalCycleTimes = [];
  for (var i = 0; i < estimates.length; i++) {
    var cycleTimesData = await GetCycleTimes.run({
      estimate: estimates[i]
    });
    var data = await CycleTimeUtils.getCycleTimes();
    var cycleTimes = data.map(item => item.cycleTime);
    globalCycleTimes.push({
      "estimate": estimates[i],
      "durations": cycleTimes
    });
    var calculations = await CycleTimeUtils.doCalculations(cycleTimes);
    stats.push({
      "estimate": estimates[i],
      "sampleSize": data.length,
      "mean": calculations.mean,
      "median": calculations.median,
      "stdDev": calculations.stdDev,
      "minimum": calculations.minimum,
      "maximum": calculations.maximum
    });
  }
  await storeValue('globalCycleTimes', globalCycleTimes);
  await storeValue('stats', stats);
  return globalCycleTimes;
}

async function () {
  const members = ["rishabhrathod01", "sumitsum", "NilanshBansal", "AmanAgarwal041", "sneha122", "rohan-arthur"];
  let data = [];
  for (var i = 0; i < members.length; i++) {
    var memberPRs = await GetPRsforAuthor.run({
      query: "repo:appsmithorg/appsmith is:pr is:closed sort:updated-desc author:" + members[i]
    });
    var obj = {};
    data.push({
      name: members[i],
      PRs: memberPRs
    });
  }
  const result = {};
  const currentDate = new Date();
  data.forEach(authorData => {
    const authorName = authorData.name;
    authorData.PRs.data.search.edges.forEach(pr => {
      const closedDate = new Date(pr.node.closedAt);
      if (closedDate >= new Date("2024-01-01T00:00:00Z") && closedDate <= currentDate) {
        const weekKey = `${ThroughputUtils.getISOWeekNumber(closedDate)}/${closedDate.getUTCFullYear()}`;
        if (!result[weekKey]) {
          result[weekKey] = {};
        }
        if (!result[weekKey][authorName]) {
          result[weekKey][authorName] = 0;
        }
        result[weekKey][authorName]++;
      }
    });
  });
  const resultArray = Object.entries(result).map(([weekKey, authorData]) => {
    return {
      week: weekKey,
      ...authorData
    };
  });
  resultArray.sort((a, b) => parseInt(a.week.split('/')[0]) - parseInt(b.week.split('/')[0]));
  if (Array.isArray(resultArray) && resultArray.length > 0) {
    console.log("from home function is array");
  } else {
    console.log("from home function is not array");
  }
  return resultArray;
}

